    * sender must be listing owner owner to extend listing ownership, OR the ownership value is in the past (current owner forfeits the listing).
    * In the forfeit case, the sender will be the new owner, and new ownership would be time credit added on top of current timestamp
    *
    * Time credit formula:
    *   C = (A / D) x 86400
    *      C (timestamp): Time credit (in seconds)
    *      A (tokens): Transfered amount to extend ownership
    *      D (tokens): Daily payment, specified by the validator
    *      86400: Total seconds in a day (60*60*24)
    */
    function extendOwnership (uint256 _amount) public {
        require(msg.sender == owner || ownership <  block.timestamp, "Listing: Unauth!");

        bool success = Token(tokenContract).handleListingTx(msg.sender, _amount, true);
        require(success, "Listing: Unsuccessful attempt!");

        uint256 existingOwnership = ownership;
        address existingOwner = owner;

        if (existingOwnership < block.timestamp) {
            owner = msg.sender;
            existingOwnership = block.timestamp;
        }

        rewardPool = rewardPool.add(_amount);
        uint256 credDays = _amount.div(dailyPayment);

        ownership = existingOwnership.add(credDays.mul(86400));
        emit OwnershipExtension(existingOwner, owner, existingOwnership, ownership);

    }

    /**
    * @dev Owner can partially withdraw the tokens they previously transfered to extend the listing ownership (and forfeit the ownership)
    * 
    * Ownership value must be in the future (larger or equal to block.timestamp)
    *
    * Ownership withdraw formula:
    *   CL = OS - TS
    *   VR = (CL / 86400) * D
    *      CL (timestamp): Time credit left
    *      OS (timestamp): Exisiting ownership
    *      TS (timestamp): Current block timestamp
    *      D  (tokens):  Daily payment, specified by the validator
    *      VR (tokens): Value to return to the owner
    *
    * `valueToReturn` shall be transfered from Funds account to user, if success the ownership value
    * should be reset to current TS, and rewardPool is decreased by `valueToReturn`
    */
    function withdraw() public {
        require(msg.sender == owner, "Listing: Unauth!");
        require(ownership >= block.timestamp, "Listing: Ownership expired!");

        uint256 valueToReturn = ((ownership - block.timestamp).div(86400)).mul(dailyPayment);

        bool success = Token(tokenContract).handleListingTx(msg.sender, valueToReturn, false);
        require(success, "Listing: Unsuccessful attempt!");

        emit Withdraw(owner, valueToReturn);

        rewardPool = rewardPool.sub(valueToReturn);
        ownership = block.timestamp;
    }

    /**
    * @dev Stakeholders can claim their reward they previously registed
    * 
    * See {calculateStakeholderReward} for the reward payout formula
    *
    * Staking status should be active (stakeholder hasnt unregistered)
    * Users must have at least `_amount` tokens in their balance
    *
    * If the transfer from the funds address to user's address fulfilled, an {Claim} event is emitted
    * rewardPool is decreased by the payout amount, and the stake start is reset to the current block.timestamp
    */
    function claimReward (uint256 _optionId) public {
        StakingModel storage userStake = stakings[_optionId][msg.sender];
        require(userStake._active, "Listing: Register first!");

        uint256 callerBalance = Token(tokenContract).balanceOf(msg.sender);
        require(callerBalance >= userStake._amount, "Listing: Insufficient balance!");

        uint256 payoutAmount = calculateStakeholderReward(_optionId, userStake);
        bool success = Token(tokenContract).handleListingTx(msg.sender, payoutAmount, false);
        require(success, "Listing: Unsuccessful attempt!");

        emit Claim(msg.sender, payoutAmount, userStake._start, block.timestamp);

        userStake._start = block.timestamp;
        rewardPool = rewardPool.sub(payoutAmount);              
    }

    /**
    * @dev User can register to be a stakeholder.
    * 
    * NOTE The key difference of this staking model is the user's token balance isnt deducted (like other staking models).
    * Users, however, need to have at least registered amount in their balance WHEN they claim rewards
    *
    * Users must have at least `_amount` tokens in their balance
    *
    * Users cant register for inactive listing
    *
    * `_amount` is the registered amount
    * `_optionId` is the option which user would like to register
    * `_increase` flag helps user increase/decrease the amount they previously registered
    *
    * Both listing total stake and option total stake is increased by `_amount`
    * Emits an {Register} event
    */
    function register (uint256 _amount, uint256 _optionId, bool _increase) public onlyActiveListing {
        require(options[_optionId]._isSet, "Listing: Option not available");
        uint256 callerBalance = Token(tokenContract).balanceOf(msg.sender);
        require(callerBalance >= _amount, "Listing: Insufficient balance!");

        StakingModel storage userStake = stakings[_optionId][msg.sender];

        userStake._amount = _increase ? userStake._amount.add(_amount) : userStake._amount.sub(_amount);
        userStake._start = block.timestamp;
        userStake._active = true;

        options[_optionId]._totalStake = options[_optionId]._totalStake.add(_amount);

        totalStake = totalStake.add(_amount);

        emit Register(msg.sender, _amount, _optionId, block.timestamp);
    }

    /**
    * @dev User can unregister their previous stake
    * 
    * Staking status should be active (stakeholder hasnt unregistered)
    *
    * Users cant register for inactive listing
    *
    * Staking info is reset to initial status
    *
    * `_optionId` is the option which user would like to unregister
    *
    * Both listing total stake and option total stake is decreased by `_amount`
    * Emits an {Unregister} event
    */
    function unregister(uint256 _optionId) public onlyActiveListing {
        StakingModel storage userStake = stakings[_optionId][msg.sender];

        require(userStake._active, "Listing: Register first!");

        options[_optionId]._totalStake = options[_optionId]._totalStake.sub(userStake._amount);

        totalStake = totalStake.sub(userStake._amount);

        userStake._amount = 0;
        userStake._active = false;
        userStake._start = 0;

        emit Unregister(msg.sender, block.timestamp);
    }

    /**
    * @dev Stakeholder reward formula
    * 
    * T    = TS * 100 / LV
    * RTd  = DA * T%
    * Ar   = ((RTd * 100 / Ap) / At) * Ax
    * Rm   = Ar * Sd

    *   T(%)      : Percentage for total token rewarding (less or equal than 86)
    *               If listing is forfeited, T is less or equal than 50
    *   TS        : Total tokens staked for the listing
    *   LV        : Listing value
    *   RTd       : Total tokens for rewarding daily for ALL stakeholder (daily avaiable tokens for claiming)
    *   DA        : Daily Payment
    *   Ap(%)     : Reward percentage value for the chosen option
    *   At(tokens): The total staked amount for an option from ALL stakeholders
    *   Ax(tokens): The staked amount for an option from the calling stakeholder (msg.sender)
    *   Ar(tokens): Available daily tokens for claiming for ONE stakeholder for one option
    *   Sd(days)  : Staked days
    *   Rm(tokens): The reward for a stakeholder after a period of time
    */
    function calculateStakeholderReward (uint256 _optionId, StakingModel storage _userStake) private view returns (uint256) {
        uint256 T = totalStake.mul(100).div(value);
        if (T > 86) {
            T = 86;
        }

        if (ownership < block.timestamp && T > 50) {
            T = 50;
        }

        uint256 RTd = dailyPayment.mul(T).div(100);

        OptionModel memory optionInfo = options[_optionId];

        uint256 Ar = (RTd.mul(optionInfo._reward).div(100)).mul(_userStake._amount).div(optionInfo._totalStake);

        uint256 Sd = (block.timestamp - _userStake._start) / 86400;

        return Ar.mul(Sd);
    }

    /**
     * @dev Modifier, making sure the current listing is active to proceed further logic
     */
    modifier onlyActiveListing() {
        (, bool _active) = Token(tokenContract).listingStatus(address(this));

        require(_active, "Listing: Inactive listing!");
        _;
    }

    /**
     * @dev Emitted when the owner extends ownership with the listing
     *
     * `_prevOwner` is the previous owner address
     * `_newOwner` is the new owner address
     * `_start` is the existing ownership
     * `_end` is when the ownership ends
     * `_amount` is the transfered amount
     */
    event OwnershipExtension (address _prevOwner, address _newOwner, uint256 _start, uint256 _end);

    /**
     * @dev Emitted when the owner withdraws tokens (forfeit ownership)
     *
     * `owner` is the owner withdrawing
     * `_valueToReturn` is the amount to return to the owner
     */
    event Withdraw (address owner, uint256 _valueToReturn);

    /** @dev Emitted when an user claiming rewards
     *
     * `_stakeholder` is the stakeholder 
     * `_reward` is the reward amount
     * `_from` -> `_to` is the staking period of time
     */
    event Claim(address _stakeholder, uint256 _reward, uint256 _from, uint256 _to);

    /** @dev Emitted when an user registers for claiming rewards
     *
     * `_stakeholder` is the stakeholder 
     * `_amount` is the registered amount
     * `_optionId` is the chosen option
     * `_start` is when the user registers
     */
    event Register(address _stakeholder, uint256 _amount, uint256 _optionId, uint256 _start);

    /** @dev Emitted when an user unregisters for claiming rewards
     *
     * `_stakeholder` is the stakeholder 
     * `_at` is when the user unregisters
     */
    event Unregister(address _stakeholder, uint _at);


}","keccak256":"0x5d0158d7809ee85ae9634228b10d9b3cdc12e67eeccfc1cf54073ddc2b435a47","license":"MIT"},"contracts/v1/ListingStorage.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/**
 * @dev This contract stores basic configurations for a listing.
 *
 * The purpose of the this is abtract the basic getters-setters away from the staking/rewards logic
 */

contract ListingStorage {

    /**
     * @dev Owner is the address pays tokens to take/maintain the ownership for a listing
     */
    address public owner;

    /**
     * @dev Listing ID is to bind the Listing NFT on the blockchain with the listing data in
     * the real world
     */
    uint256 public listingId;

    /**
     * @dev Validator is the authorized account to update the listing configurations
     */
    address public validator;

    /**
     * @dev Token contract should be the contract create this listing
     */
    address public tokenContract;

    /**
     * @dev Ownership value should be in the unix timestamp, determinating if the listing is forfeited by the owner
     * (ownership < block/timestamp) and ready to be taken by another owner user.
     */
    uint256 public ownership;

    /**
     * @dev The market value of the listing in real world. Determined by the board, mutable by the validator
     */
    uint256 public value;

    /**
     * @dev Daily payment determines how long the owner has the ownership over the listing with the tokens they pay.
     */
    uint256 public dailyPayment;

    /**
     * @dev Workers are addresses authorized by the owner to use the listing in the real world
     */
    mapping (address => bool) public workers;

    /**
     * @dev Options are business activities in real world. Each options has a rewards percentage (sum up to 100);
     * Reward value for each option is set by the validator. 
     *
     * `_isSet` flag is default to false, until the option is set by the validator
     * The flag is to prevent users from participating in the activities which arent set up by the validator.
     *
     * Everytime an user participates in an activity, the `_totalStake` is increased by the registered amount
     */
    struct OptionModel {
        uint256 _totalStake;
        uint256 _reward;
        bool _isSet;
    }
    mapping (uint => OptionModel) public options;

    /**
     * @dev Option reward value is set up by and only by the validator
     * Reward should not exceed 100;
     * The sum of reward values from all options should be 100;
     * 
     * `isSet` is toggled to true for options which were setup
     *
     * For example A, B, C, D are options. Ar, Br, Cr, Dr are the reward values respectively
     * The validator should set up the option carefully, so Ar + Br + Cr + Dr = 100
     */
    function setupOptionReward (uint256 _optionId, uint256 _reward) public onlyValidator {
        require(_reward <= 100, "Listing: Invalid reward value");
        options[_optionId]._reward = _reward;
        options[_optionId]._isSet = true;
    }   

    /**
     * @dev Validator can update the listing ID
     */
    function updatelistingId (uint256 _listingId) public onlyValidator {
        listingId = _listingId;
    }

    /**
     * @dev Validator can update the owner value
     * Owner value can only changeable if the current owner has forfeited the listing
     */
    function updateOwner (address _newOwner) public onlyValidator {
        require(ownership < block.timestamp, "Ownership not expired!");
        owner = _newOwner;
    }

    /**
     * @dev Validator can update the listing value to reflect the real estate price in the real world
     */
    function updateValue (uint256 _value) public onlyValidator {
        value = _value;
    }

    /**
     * @dev Validator can update the daily payment value to reflect the real estate price in the real world
     */
    function updateDailyPayment (uint256 _dailyPayment) public onlyValidator {
        dailyPayment = _dailyPayment;
    }

    /**
     * @dev Validator can update themselves
     */
    function updateValidator (address _validator) public onlyValidator {
        validator = _validator;
    }

    /**
     * @dev Owner can update worker status. Which means they can choose/unchoose who
     * can use the listing in real world
     */
    function updateWorker(address _worker) public {
        require(msg.sender == owner, "Listing: Unauth!");
        require(ownership >= block.timestamp, "Listing: Ownership expired");
        workers[_worker] = !workers[_worker];
        emit UpdateWorker(_worker, workers[_worker]);
    }

    /**
     * @dev Modifier, restricting access to only the validator
     */
    modifier onlyValidator() {
        require(msg.sender == validator, "Listing: Unauth!");
        _;
    }

    /**
     * @dev Emitted when the worker status is updated.
     *
     * `_worker` is the updated address, `_isAuthorized` is the new status
     */
    event UpdateWorker(address _worker, bool _isAuthorized);
}
","keccak256":"0xc9449297824674a2478c99536c1d3497ded525cef033d2f4ff8273cd9a695a83","license":"MIT"},"contracts/v1/Token.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./Listing.sol";

contract Token is Initializable, ERC20Upgradeable, AccessControlUpgradeable, UUPSUpgradeable {

    /**
     * @dev Validators are authorized accounts to handle listing
     */
    bytes32 public constant VALIDATOR = keccak256("VALIDATOR");

    /**
     * @dev The funds address for paying out rewards and receiving payments
     */
    address public stakingAddress;

    /**
     * @dev Deployer has the default roles of DEFAULT_ADMIN_ROLE and VALIDATOR
     * Staking address is set here
     */

    /**
     * @dev A listing address must be both `_isCreated` and `_active` to be able to operate.
     * Otherwise malicious accounts can call handleListingTx functions
     *
     * The `_isCreated` property is immutable. It's remains true with the addresses of listings 
     * created by the validator,
     *
     * The `_active` property is mutable by the validators.
     */
    struct ListingStatusModel {
        bool _isCreated;
        bool _active;
    }
    mapping (address => ListingStatusModel) public listingStatus;

    function initialize(address _stakingAddr) public initializer {

        __UUPSUpgradeable_init();
        __AccessControl_init();
        __ERC20_init("ANFT Token", "ANFT");

        _mint(_msgSender(),  1232000000 * 10 ** decimals());
        stakingAddress = _stakingAddr;
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(VALIDATOR, _msgSender());
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyRole(DEFAULT_ADMIN_ROLE)
    {}

    /**
     * @dev Handle transactions from listings and only from listings
     * 
     * If `_in` is true, _amount will be transfered from `_userAddr` to staking address
     * Otherwise, `_amount` will be transfered from staking address to `_userAddr`
     *
     * Returns true if the transation is success
     *
     * Emits a {Transfer} event from IERC20
     */
    function handleListingTx (address _userAddr, uint256 _amount, bool _in) external returns (bool) {
        require(listingStatus[_msgSender()]._isCreated, "Token: Invalid Listing");
        require(listingStatus[_msgSender()]._active, "Token: Inactive Listing");
        address sender;
        address recipient;
        (sender, recipient) = _in ? (_userAddr, stakingAddress) : (stakingAddress, _userAddr);
        super._transfer(sender, recipient, _amount);
        return true;
    }

    /**
     * @dev Create a new listing (the tokenized version of a real estate product)
     * 
     * Restricted to only validators
     *
     * A newly created listing is set with two properties: `_isCreated` and `_active`
     *
     * Listing validator shall be the validator address making the transaction. Owner of the listing shall be
     * the specified address
     * 
     * Emits a {ListingCreation} event
     */
    function createListing(address _owner, uint256 _listingId) public {
        require(hasRole(VALIDATOR, _msgSender()), "Token: Unauthorized");
        Listing newListing = new Listing(_msgSender(), _owner, _listingId);
        emit ListingCreation(_msgSender(), _owner, address(newListing));    
        listingStatus[address(newListing)]._isCreated = true;    
        listingStatus[address(newListing)]._active = true;    
    }

    /**
     * @dev Toggle listing `_active` status. Shall be useful if there's a listing no longer in operation
     *
     * Restricted to only validators
     *
     * The `_listingAddr` must be the address of a created listing
     * 
     */
    function toggleListingStatus (address _listingAddr) public {
        require(hasRole(VALIDATOR, _msgSender()), "Token: Unauthorized");
        require(listingStatus[_listingAddr]._isCreated, "Token: Invalid Listing");
        listingStatus[_listingAddr]._active = !listingStatus[_listingAddr]._active;
    }

    /**
     * @dev Update the funds address. Shall be handy if there's a need in changing the initial address,
     * or the previous address is compromised.
     */    
    function updateStakingAddress (address _stakingAddr) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "Token: Unauthorized");
        stakingAddress = _stakingAddr;
        emit UpdateStakingAddr(_stakingAddr);
    }

    /**
     * @dev Emitted a new Listing is created. `_validator` should be the caller, 
     * `_owner` should be the specified address and `_listingAddress` is the
     * newly created listing address
     */
    event ListingCreation(address _validator, address _owner, address _listingAddress);

    /**
     * @dev Emitted when the staking address is updated, `_stakingAddr` should be the new address 
     */
    event UpdateStakingAddr(address _stakingAddr);

}


